settings.custom_css:
selector{
    width: 100% !important;
}
body.header-show selector{
    border-bottom: 1px solid transparent;
    transition: all 0.3s ease-in-out !important;
}
body.header-show selector{
    border-bottom: 1px solid rgba(0,0,0,.1);
    box-shadow: 2px 0px 5px 0px rgba(0,0,0,.2);
}
selector .elementor-widget-image:nth-child(2){
    display: none;
}
body.night selector .elementor-widget-image:nth-child(1){
    display: none;
}
body.night selector .elementor-widget-image:nth-child(2){
    display: block;
}



body.elementor-editor-active selector .elementor-widget-image:nth-child(5),
body.elementor-editor-preview selector .elementor-widget-image:nth-child(5){
    display: none;
}
body.elementor-editor-active.night selector .elementor-widget-image:nth-child(4),
body.elementor-editor-preview.night selector .elementor-widget-image:nth-child(4){
    display: none;
}
body.elementor-editor-active.night selector .elementor-widget-image:nth-child(5),
body.elementor-editor-preview.night selector .elementor-widget-image:nth-child(5){
    display: block;
}

selector .elementor-icon-list-item.active span{
    color: #6051BC;
}
selector a:focus{
    outline: none;
}

@media (max-width: 1024px){
selector{
    display: none;
}
selector ul.elementor-icon-list-items.elementor-inline-items{
    flex-direction: column;
}
selector .elementor-icon-list-item{
    margin: 0 0 20px;
}
selector .elementor-icon-list-item:last-child{
    margin: 0;
}
}

@media (min-width: 1025px){
selector{
    display: block !important;
}
}

body{
    --body-bg: #1A1625;
    --white-one: #f7f0f7;
    --white-two: #bebcbc;
    --n-card: #211D33;
    --n-blue: #705FDC;
}

selector{
    cursor: pointer;
}
body,
body.night h2,
body.night h3,
body.night p{
    transition: all 0.3s ease-in-out !important;
}
body.night{
    background-color: var(--body-bg) !important;
}
body.night h2, body.night .elementor-testimonial__name, body.night .elementor-counter-number-wrapper, body.night .elementor-button, body.night .elementor-title, body.night h3.elementor-icon-box-title,
body.night .my-nav .elementor-icon-list-item span{
    color: var(--white-one) !important;
}

body.night h3, body.night p, body.night .elementor-counter-title, body.night .elementor-testimonial__text, body.night .elementor-field, body.night .day-card .elementor-button-icon{
    color: var(--white-two) !important;
}

body.night .day-card{
    background-color: var(--n-card) !important;
}
body.night .day-card .elementor-button, body.night .elementor-icon,
body.night .my-nav .elementor-icon-list-item.active span, body.night .elementor-location-popup h2{
    color: var(--n-blue) !important;
}
